mapper.insert:

	SELECT NVL(MAX(bno),999)+1
	FROM board   

	INSERT INTO board (bno
					    ,title
						,contents
						,writer
						,regdate
						
						,updatedate
						
						,image
						)
			
			VALUES	   (#{bno}
						,#{title}
						,#{contents}
						,#{writer}
						,#{regdate}
						
						,#{updatedate}
						
						,#{image}
						)

mapper.update:
UPDATE board
SET
   title = #{title}
  ,contents = #{contents}
  ,writer = #{writer}
  ,regdate = #{regdate}
  ,updatedate = sysdate
  
WHERE bno = #{bno}
